export const formatPrice = (number) => {
    return new Intl.NumberFormat('en-EU',{
        style:'currency',
        currency:'EUR'
    }).format(number)
}

export const getUniqueValues = (data, type) => {
    let unique = data.map((item)=> item[type])
    if(type === 'colors'){
        unique = unique.flat()
    }
    return ['all',...new Set(unique)]
}
